import React, { useEffect, useState } from 'react';

function UserTable() {
  const [users, setUsers] = useState([]);
  const [addresses, setAddresses] = useState([]); // Renamed for clarity

  const fetchData = async () => {
    try {
      const response1 = await fetch('http://localhost:8000/api/user-name'); // Use http instead of https for local
      const name = await response1.json();
      const response2 = await fetch('http://localhost:8000/api/address-name');
      const addresses = await response2.json(); 

      setUsers(name);
      setAddresses(addresses);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  console.log(users, addresses);
  return (
    <div>
      <h1>User List</h1>
      <table border="1">
        <thead>
          <tr>
            <th>Name</th>
            <th>Address</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, index) => (
            <tr key={index}>
              <td>{user.name}</td>
              <td>{addresses[index] ? addresses[index].address : "N/A"}</td> {/* Match by index */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default UserTable;
